package com.c2.training;

public class DefaultValue {

	static boolean z;
	static byte b;
	static short s;
	static int a;
	static long l;
	static float f;
	static double d;
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.print("Default value for boolean: ");
		System.out.println(z);
		System.out.print("Default value for byte: ");
		System.out.println(b);
		System.out.print("Default value for short: ");
		System.out.println(s);
		System.out.print("Default value for int: ");
		System.out.println(a);
		System.out.print("Default value for long: ");
		System.out.println(l);
		System.out.print("Default value for float: ");
		System.out.println(f);
		System.out.print("Default value for double: ");
		System.out.println(d);
	}

}
